import{o as l,g as i,G as p}from"./framework.e6884fad.js";const _='{"title":"\u8BFB\u300A\u9AD8\u6548\u7A0B\u5E8F\u5458\u768445\u4E2A\u4E60\u60EF\u300B","description":"","frontmatter":{},"relativePath":"book/45-habit.md","lastUpdated":1684203866043}',t={},u=p('<h1 id="\u8BFB\u300A\u9AD8\u6548\u7A0B\u5E8F\u5458\u768445\u4E2A\u4E60\u60EF\u300B"><a class="header-anchor" href="#\u8BFB\u300A\u9AD8\u6548\u7A0B\u5E8F\u5458\u768445\u4E2A\u4E60\u60EF\u300B" aria-hidden="true">#</a> \u8BFB\u300A\u9AD8\u6548\u7A0B\u5E8F\u5458\u768445\u4E2A\u4E60\u60EF\u300B</h1><div class="tip custom-block"><p class="custom-block-title">\u7B80\u4ECB</p><p>\u8FD9\u672C\u4E66\u8BB2\u7684\u4E3B\u8981\u662F\u654F\u6377\u5F00\u53D1\u7684\u597D\u4E60\u60EF\u3002\u5982\u679C\u4F60\u662F\u5728\u4E00\u4E2A\u5C0F\u578B\u56E2\u961F\u505A\u5F00\u53D1\uFF0C\u90A3\u4E48\u8FD9\u672C\u4E66\u5C06\u5BF9\u4F60\u5341\u5206\u6709\u76CA\u3002</p><p>\u4E66\u4E2D\u5BF9\u654F\u6377\u5F00\u53D1\u6709\u4E00\u53E5\u7CBE\u8F9F\u6982\u51B5\uFF1A\u654F\u6377\u5F00\u53D1\u662F\u5728\u4E00\u4E2A<strong>\u9AD8\u5EA6\u534F\u4F5C</strong>\u7684\u73AF\u5883\u4E2D\uFF0C<strong>\u4E0D\u65AD</strong>\u5730\u4F7F\u7528<strong>\u53CD\u9988</strong>\u8FDB\u884C<strong>\u81EA\u6211\u8C03\u6574\u548C\u5B8C\u5584</strong>\u3002</p><p>\u4E0B\u9762\u5C06\u662F\u4E00\u4E9B\u4E66\u4E2D\u7684\u6458\u5F55\uFF0C\u5C06\u5939\u6742\u6211\u4E2A\u4EBA\u7ED3\u5408\u5B9E\u8DF5\uFF08\u6E38\u620F\u5F00\u53D1\uFF09\u7684\u4E00\u4E9B\u601D\u8003\u3002</p></div><ol><li><p>\u505A\u4E8B</p><ul><li><p>\u6307\u8D23\u5E76\u4E0D\u80FD\u4FEE\u590Dbug\uFF0C\u9047\u5230\u95EE\u9898\u65F6\u4E0D\u5E94\u8BE5\u4E92\u76F8\u8FFD\u7A76\u8D23\u4EFB\uFF0C\u800C\u662F\u5E94\u8BE5\u76F4\u9762\u95EE\u9898\uFF0C\u89E3\u51B3\u95EE\u9898\u3002</p></li><li><p>\u4E92\u76F8\u6307\u8D23\u5FC5\u7136\u4F1A\u5BFC\u81F4\u56E2\u961F\u58EB\u6C14\u4E0B\u964D\uFF0C<strong>\u5BF9\u4E8B\u800C\u4E0D\u5BF9\u4EBA</strong>\uFF0C\u4F18\u5148\u89E3\u51B3\u95EE\u9898\uFF08\u77DB\u76FE\uFF0Cbug\uFF09\u624D\u662F\u738B\u9053\u3002</p></li></ul></li><li><p>\u6B32\u901F\u5219\u4E0D\u8FBE</p><ul><li>Debug\u4E0D\u8981\u5815\u5165\u7B80\u5355\u7684\u5FEB\u901F\u4FEE\u590D\u4E2D\uFF0C\u800C\u5E94\u8BE5<strong>\u7406\u89E3\u6240\u505A\u7684\u4FEE\u6539</strong>\uFF0C\u5E76\u4E14\u8BA9\u4EE3\u7801\u4FDD\u6301\u6574\u6D01\u3002\u603B\u4E4B\uFF0C\u4E0D\u8981\u6025\u4E8E\u4FEE\u590D\u4E00\u6BB5\u4F60\u6CA1\u6709\u7406\u89E3\u7684\u4EE3\u7801\u3002</li></ul></li><li><p>\u8DDF\u8E2A\u53D8\u5316</p><ul><li><p>\u6BCF\u5929\u82B1\u4E00\u70B9\u65F6\u95F4\u6765\u5B66\u4E60\u65B0\u7684\u6280\u672F\u3002\u4F60\u4E0D\u9700\u8981\u7CBE\u901A\u6240\u6709\u6280\u672F\uFF0C\u4F46\u662F\u9700\u8981\u4E86\u89E3\u884C\u4E1A\u7684\u52A8\u5411\uFF0C\u4EE5\u89C4\u5212\u4F60\u7684\u804C\u4E1A\u751F\u6DAF\u3002</p></li><li><p>\u4E00\u5473\u7740\u5B88\u7740\u8FC7\u65F6\u7684\u6280\u672F\u53EF\u80FD\u4F1A\u4EA7\u751F\u4F9D\u8D56\uFF0C\u96BE\u4EE5\u8FDB\u6B65\u3002</p></li></ul></li><li><p>\u628A\u63E1\u5F00\u53D1\u8282\u594F</p><ul><li><p>\u7AD9\u7ACB\u8FDB\u884C\u4F1A\u8BAE\uFF0C\u4EE5\u8282\u7701\u4F1A\u8BAE\u65F6\u95F4\u3002</p></li><li><p>\u4FDD\u6301\u8212\u670D\u7684\u8FED\u4EE3\u5468\u671F\uFF0C\u4E0D\u8981\u641E\u5F97\u7ECF\u5E38\u52A0\u73ED\u3002</p></li><li><p>\u5728\u6BCF\u5929\u7ED3\u675F\u65F6\uFF0C\u6D4B\u8BD5\uFF0C\u63D0\u4EA4\u4EE3\u7801\u3002</p></li><li><p>\u52A0\u73ED\u4E0E\u5426\u901A\u5E38\u662F\u4E00\u4E2A\u6548\u7387\u95EE\u9898\uFF0C\u5236\u5B9A\u597D\u5408\u9002\u7684\u5DE5\u4F5C\u91CF\uFF0C\u7136\u540E\u4E13\u6CE8\u53BB\u5B8C\u6210\u5B83\u3002</p></li></ul></li><li><p>\u5408\u7406\u4F7F\u7528\u6280\u672F</p><ul><li><p>\u4E0D\u8981\u91CD\u590D\u9020\u8F6E\u5B50\u3002\u4E0D\u8981\u5F00\u53D1\u4F60\u80FD\u591F\u4E0B\u8F7D\u5230\u7684\u4E1C\u897F\u3002</p></li><li><p>\u5982\u679C\u6709\u522B\u4EBA\u5199\u597D\u7684\uFF0C\u53EF\u9760\u7684\u5DE5\u5177\uFF0C\u5219\u5229\u7528\u5DE5\u5177\u3002</p></li></ul></li><li><p>\u4FDD\u6301\u53EF\u4EE5\u53D1\u5E03</p><ul><li><p><strong>\u6BCF\u4E00\u6B21\u63D0\u4EA4\u5230\u4EE3\u7801\u4ED3\u5E93\u7684\u4EE3\u7801\u5FC5\u987B\u4FDD\u8BC1\u6B63\u786E</strong>\u3002</p></li><li><p>\u8FD9\u4E00\u70B9\u53EF\u4EE5\u901A\u8FC7<strong>\u6301\u7EED\u96C6\u6210\u5DE5\u5177</strong>\u8FBE\u5230\uFF0C\u6BD4\u5982jekins\u3002</p></li><li><p>\u540C\u65F6\u4E5F\u9700\u8981\u4F60\u5BF9\u6BCF\u4E00\u6B21\u63D0\u4EA4\u8FDB\u884C\u6D4B\u8BD5\uFF0C\u5BF9\u4EE3\u7801<strong>\u8D1F\u8D23</strong>\u3002</p></li></ul></li><li><p>\u901A\u8FC7\u6F14\u793A\u83B7\u5F97\u53CD\u9988</p><ul><li>\u9700\u6C42\u662F\u4F1A\u4E0D\u65AD\u53D8\u66F4\u7684\u3002\u6BCF\u6B21\u8FED\u4EE3\u8FC7\u540E\u90FD\u9700\u8981\u8FDB\u884C\u4E00\u6B21\u6F14\u793A\uFF08DEMO\uFF09\uFF0C\u83B7\u53D6\u53CD\u9988\uFF0C\u4EE5\u4FEE\u6B63\u4E0B\u4E00\u6B21\u8FED\u4EE3\u3002</li></ul></li><li><p>\u4F7F\u7528\u77ED\u8FED\u4EE3\uFF0C\u589E\u91CF\u53D1\u5E03</p><ul><li><p>\u5C06\u8FED\u4EE3\u5468\u671F\u5C3D\u53EF\u80FD\u7F29\u77ED\uFF0C\u8FD9\u6837\u80FD\u8BA9\u4EBA\u611F\u5230\u5341\u5206\u6709\u6548\u7387\uFF0C\u4E14\u80FD\u9891\u7E41\u83B7\u5F97\u53CD\u9988\u3002</p></li><li><p>\u6BCF\u4E00\u6B21\u7684\u8FED\u4EE3\u5E94\u8BE5\u6709\u4E00\u4E2A\u5177\u4F53\u7684\u5C0F\u76EE\u6807\uFF0C\u901A\u8FC7<strong>\u589E\u91CF\u5F0F</strong>\u7684\u800C\u4E0D\u662F\u4E00\u8E74\u800C\u5C31\u7684\u6765\u68C0\u67E5\u6BCF\u4E2A\u76EE\u6807\u7684\u5B8C\u6210\u60C5\u51B5\u3002</p></li></ul></li><li><p>\u5EA6\u91CF\u771F\u5B9E\u7684\u8FDB\u5EA6</p><ul><li><p>\u901A\u8FC7\u5F85\u529E\u4E8B\u9879\uFF0C\u6BCF\u4E00\u5929\u7ED3\u675F\u65F6\uFF0C\u786E\u5B9A\u54EA\u4E9B\u4EFB\u52A1\u5B8C\u6210\uFF0C\u54EA\u4E9B\u4EFB\u52A1\u6CA1\u6709\u5B8C\u6210\u3002</p></li><li><p>\u5C06\u6CA1\u6709\u5B8C\u6210\u7684\u4EFB\u52A1\u5217\u4E0A\u4F18\u5148\u7EA7\uFF0C\u7136\u540E\u4F30\u7B97\u5269\u4E0B\u7684\u5DE5\u4F5C\u91CF\uFF0C\u4E00\u822C<strong>\u4EE5\u5C0F\u65F6</strong>\u4E3A\u7C92\u5EA6\u3002</p></li></ul></li><li><p>\u4EE3\u7801\u8981\u6E05\u6670\u5730\u8868\u8FBE\u610F\u56FE</p><ul><li><p>\u4EE3\u7801\u7684\u53EF\u8BFB\u6027\u5341\u5206\u91CD\u8981\uFF0C\u5C24\u5176\u5728\u591A\u4EBA\u5408\u4F5C\u7684\u654F\u6377\u5F00\u53D1\u4E2D\u3002</p></li><li><p>\u4EE3\u7801\u4E0D\u5E94\u8BE5\u70AB\u6280\uFF0C\u5E94\u8BE5\u6E05\u6670\u660E\u786E\u8868\u660E\u610F\u56FE\u3002</p></li><li><p>\u4EE3\u7801\u53EF\u4EE5\u901A\u8FC7\u547D\u540D\u7684\u89C4\u8303\u6765\u8FDB\u884C\u81EA\u8BF4\u660E\uFF0C\u800C\u4E0D\u9700\u8981\u4F9D\u9760\u591A\u4F59\u7684\u6CE8\u91CA\u3002</p></li><li><p>\u6CE8\u91CA\u6700\u597D\u5199\u5728\u51FD\u6570\u63A5\u53E3\u548C\u6781\u5176\u590D\u6742\uFF08\u4E00\u822C\u9700\u8981\u91CD\u6784\uFF09\u903B\u8F91\u5904\u3002</p></li></ul></li><li><p>\u52A8\u6001\u8BC4\u4F30\u53D6\u820D</p><ul><li><p>\u4E00\u4EF6\u4E8B\u60C5\u51E0\u4E4E\u53EF\u4EE5\u6C38\u8FDC\u4F18\u5316\u4E0B\u53BB\uFF0C\u6240\u4EE5\u4F60\u9700\u8981\u5224\u65AD\u4E00\u4E2A\u5E73\u8861\u7684\u70B9\uFF0C\u4F18\u5316\u5230\u4E00\u5B9A\u7A0B\u5EA6\u5C31\u505C\u6B62\u3002</p></li><li><p>\u53E6\u5916\uFF0C<strong>\u8FC7\u65E9\u4F18\u5316\u662F\u4E07\u6076\u4E4B\u6E90</strong>\u3002\u56E0\u4E3A\u8FC7\u65E9\u4F18\u5316\u6709\u53EF\u80FD\u5728\u672A\u6765\u5E76\u4E0D\u9002\u7528\uFF0C\u7136\u540E\u63D0\u524D\u7684\u4F18\u5316\u589E\u52A0\u4E86\u4EE3\u7801\u7684\u590D\u6742\u5EA6\u548C\u7406\u89E3\u6210\u672C\u3002</p></li></ul></li><li><p>\u589E\u91CF\u5F0F\u7F16\u7A0B</p><ul><li><p>\u4E0D\u8981\u8FDE\u7EED\u51E0\u5C0F\u65F6\u7684\u8FDB\u884C\u7F16\u5199\u4EE3\u7801\u3002</p></li><li><p>\u5C06\u5927\u4EFB\u52A1\u5206\u89E3\u4E3A\u5C0F\u95EE\u9898\u3002\u7136\u540E\u4E00\u4E2A\u4E2A\u7684\u53BB\u7F16\u5199\u76F8\u5173\u4EE3\u7801\u3002</p></li><li><p>\u5199\u5B8C\u4E00\u4E2A\u5C0F\u95EE\u9898\u7684\u4EE3\u7801\u6BB5\u540E\uFF0C\u7F16\u8BD1\u8FD0\u884C\u4EE5\u4FDD\u8BC1\u6B63\u786E\u6027\uFF0C\u7136\u540E\u4F11\u606F\u4E00\u6BB5\u65F6\u95F4\uFF0C\u518D\u7EE7\u7EED\u3002</p></li></ul></li><li><p>\u7F16\u5199\u5185\u805A\u7684\u4EE3\u7801</p><ul><li><p>\u5C06\u7C7B\u7684\u529F\u80FD\u5C3D\u91CF\u96C6\u4E2D\uFF0C\u4E0D\u8981\u521B\u5EFA\u65E0\u6240\u4E0D\u5305\u7684\u5927\u6742\u70E9\u7684\u7C7B\u3002</p></li><li><p>\u5C06\u804C\u8D23\u5C3D\u53EF\u80FD\u660E\u786E\uFF0C\u96C6\u4E2D\uFF1A\u6BCF\u4E2A\u7C7B\u53EA\u505A\u4E00\u4EF6\u4E8B\u60C5\uFF0C\u7136\u540E\u628A\u5B83\u505A\u597D\u3002\u8FD9\u6837bug\u5F88\u5BB9\u6613\u8DDF\u8E2A\uFF0C\u4EE3\u7801\u4E5F\u5341\u5206\u5BB9\u6613\u4FEE\u6539\u3002</p></li></ul></li><li><p>\u544A\u77E5\uFF0C\u4E0D\u8981\u8BE2\u95EE</p><ul><li>\u5C06\u547D\u4EE4\uFF08\u53EF\u80FD\u5BF9\u6570\u636E\u6709\u6539\u52A8\uFF09\u548C\u67E5\u8BE2\uFF08\u53EA\u8BFB\uFF0C\u4E0D\u6539\u52A8\uFF09\u5206\u79BB\u3002\u5426\u5219\u5F88\u5BB9\u6613\u51FAbug\u3002</li></ul></li><li><p>\u95EE\u9898\u89E3\u51B3\u65E5\u5FD7</p><ul><li>\u5EFA\u7ACB\u4E00\u4E2A\u95EE\u9898\uFF08\u5751\uFF09\u548C\u5176\u89E3\u51B3\u65B9\u6848\u7684\u5907\u5FD8\u5F55\u3002\u4EE5\u9632\u6B62\u8D9F\u4E24\u6B21\u4E00\u6837\u7684\u5751\u3002</li></ul></li><li><p>\u5BF9\u95EE\u9898\u5404\u4E2A\u51FB\u7834</p><ul><li><p>\u5C06\u95EE\u9898\u8FDB\u884C\u5206\u79BB\u3002\u6BD4\u5982\u53EF\u4EE5\u7528<strong>\u4E8C\u5206\u6CD5</strong>\u6765\u5B9A\u4F4Dbug\uFF0C\u5C06\u4EE3\u7801\u7A7A\u95F4\u5206\u5272\u6210\u4E24\u534A\uFF0C\u7136\u540E\u5206\u79BB\u5176\u4E2D\u4E00\u534A\uFF0C\u770Bbug\u662F\u5426\u5728\u53E6\u5916\u4E00\u534A\u4E2D\u3002</p></li><li><p>debug\u65F6\uFF0C\u8981\u5C06\u95EE\u9898\u57DF\u4E0E\u5468\u8FB9\u9694\u79BB\u5F00\u6765\uFF0C\u627E\u5230\u4E86bug\u539F\u56E0\uFF0Cdebug\u5C31\u5B8C\u6210\u4E86\u4E00\u5927\u534A\u3002</p></li></ul></li><li><p>\u63D0\u4F9B\u6709\u7528\u7684\u9519\u8BEF\u4FE1\u606F</p><ul><li>\u7A0B\u5E8F\u53D1\u751F\u9519\u8BEF\u65F6\uFF0C\u5982\u679C\u5728\u5F00\u53D1\u9636\u6BB5\uFF0C\u5E94\u8BE5\u7ED9\u51FA\u660E\u663E\u800C\u8BE6\u7EC6\u7684\u62A5\u9519\u5806\u6808\uFF1B\u5982\u679C\u5728\u4EA7\u54C1\u4EA4\u4ED8\u9636\u6BB5\uFF0C\u5219\u5E94\u8BE5\u8BB0\u5F55\u4E0Blog\uFF0C\u5E76\u627E\u5230\u5408\u9002\u7684\u65F6\u673A\u53D1\u9001\u56DE\u6765\u3002</li></ul></li><li><p>\u6BCF\u65E5\u7ACB\u4F1A</p><ul><li><p>\u6BCF\u5929\u65E9\u4E0A\u4E0A\u73ED\u524D\uFF0C\u5148\u5F00\u7AD9\u7ACB\u4F1A\u8BAE\uFF0C\u56DE\u7B54\u4E09\u4E2A\u95EE\u9898\uFF1A</p><ul><li>\u6628\u5929\u7684\u6536\u83B7\uFF1F</li><li>\u4ECA\u5929\u7684\u8BA1\u5212\uFF1F</li><li>\u5F53\u524D\u6709\u54EA\u4E9B\u95EE\u9898\u548C\u963B\u788D\uFF1F</li></ul></li><li><p>\u7ACB\u4F1A\u65F6\u95F4\u4E00\u822C\u572810-15\u5206\u949F\u6BD4\u8F83\u7406\u60F3\u3002</p></li><li><p>\u5C0F\u578B\u56E2\u961F\u53EF\u80FD\u4E0D\u9700\u8981\u6BCF\u5929\u90FD\u78B0\u5934\uFF0C\u4E24\u5929\u4E00\u6B21\u6216\u8005\u4E00\u5468\u4E24\u6B21\u4E5F\u53EF\u4EE5\u3002</p></li><li><p>\u7ACB\u4F1A\u4E0D\u8981\u8FC7\u591A\u7684\u6DF1\u5165\u7EC6\u8282\uFF0C\u7ED9\u51FA\u5177\u4F53\u8FDB\u5EA6\u5373\u53EF\u3002</p></li></ul></li><li><p>\u4FDD\u6301\u7B80\u5355</p><ul><li><p>\u4F18\u5148\u4F7F\u7528\u6700\u7B80\u5355\u7684\u6280\u672F\u3002\u56E0\u4E3A\u7B80\u5355\u7684\u4EE3\u7801\u66F4\u80FD\u4E00\u773C\u770B\u51FA\u95EE\u9898\u3002</p></li><li><p>\u9664\u975E\u6709\u4E0D\u53EF\u8FA9\u9A73\u7684\u539F\u56E0\uFF0C\u5426\u5219\u4E0D\u8981\u4F7F\u7528\u590D\u6742\u7684\u6A21\u5F0F\u548C\u9AD8\u96BE\u5EA6\u6280\u672F\uFF08\u6307\u9488\uFF09\u7C7B\u7684\u4E1C\u897F\u3002</p></li><li><p>\u7B80\u5355\u7684\u4EE3\u7801\u610F\u5473\u7740\u6CA1\u6709\u91CD\u590D\u4EE3\u7801\u548C\u591A\u4F59\u4EE3\u7801\uFF0C\u4E14\u4ECD\u7136\u80FD\u5B8C\u6210\u5168\u90E8\u529F\u80FD\u3002</p></li></ul></li><li><p>\u6559\u5B66\u76F8\u957F</p><ul><li><p>\u901A\u8FC7\u8BE6\u7EC6\u89E3\u91CA\u81EA\u5DF1\u77E5\u9053\u7684\u4E1C\u897F\uFF0C\u4E5F\u53EF\u4EE5\u4F7F\u81EA\u5DF1\u7684\u7406\u89E3\u66F4\u52A0\u6DF1\u5165\u3002</p></li><li><p>\u5206\u4EAB\u53EF\u4EE5\u63D0\u9AD8\u961F\u53CB\u548C\u81EA\u5DF1\u7684\u80FD\u529B\u548C\u6C34\u5E73\uFF0C\u4E3A\u56E2\u961F\u589E\u503C\u3002</p></li><li><p>\u505A\u4EE3\u7801\u590D\u67E5</p></li></ul></li><li><p>\u590D\u67E5\u7684checklist\uFF1A</p><ul><li>\u4EE3\u7801\u662F\u5426\u80FD\u88AB\u8BFB\u61C2\u548C\u7406\u89E3\uFF1F</li><li>\u4EE3\u7801\u662F\u5426\u6709\u660E\u663E\u7684\u9519\u8BEF\uFF1F</li><li>\u4EE3\u7801\u662F\u5426\u4F1A\u5BF9\u5176\u4ED6\u4EE3\u7801\u4EA7\u751F\u526F\u4F5C\u7528\uFF1F</li><li>\u662F\u5426\u5B58\u5728\u91CD\u590D\u4EE3\u7801\uFF1F</li><li>\u662F\u5426\u5B58\u5728\u6539\u8FDB\u548C\u4F18\u5316\u7A7A\u95F4\uFF1F</li><li>\u4EE3\u7801\u590D\u67E5\u5FC5\u987B\u7ED9\u51FA\u53CD\u9988\uFF0C\u7136\u540E\u6BCF\u4E2A\u4EBA\u6839\u636E\u53CD\u9988\u91C7\u53D6\u884C\u52A8\u3002</li></ul></li></ol>',3);function o(s,r,n,g,e,a){return l(),i("div",null,[u])}t.render=o;export{_ as __pageData,t as default};
