import{o as p,g as a,G as e}from"./framework.e6884fad.js";const l='{"title":"","description":"","frontmatter":{},"relativePath":"book/hao.md","lastUpdated":1684201617048}',o={},t=e("<p>1\u3001\u7A0B\u5E8F\u5458\u7528\u81EA\u5DF1\u7684\u6280\u672F\u53D8\u73B0\uFF0C\u5176\u5B9E\u662F\u4E00\u4EF6\u5929\u7ECF\u5730\u4E49\u7684\u4E8B\u513F\u3002\u5199\u7A0B\u5E8F\u662F\u4E00\u95E8\u201C\u624B\u827A\u6D3B\u201D\uFF0C\u4F5C\u4E3A\u624B\u827A\u4EBA\uFF0C\u7A0B\u5E8F\u5458\u5F53\u7136\u53EF\u4EE5\u505A\u5230\u9760\u81EA\u5DF1\u7684\u624B\u827A\u548C\u6280\u80FD\u517B\u6D3B\u81EA\u5DF1\u3002\u7136\u800C\uFF0C\u73B0\u5728\u5F88\u591A\u624B\u827A\u4EBA\u7A0B\u5E8F\u5458\u5374\u8BF4\u81EA\u5DF1\u662F\u201C\u7801\u519C\u201D\uFF0C\u7F16\u7801\u7684\u519C\u6C11\u5DE5\uFF0C\u5728\u5DE5\u4F5C\u4E0A\u88AB\u5404\u79CD\u4F7F\u5524\uFF0C\u5404\u79CD\u52A0\u73ED\uFF0C\u7D2F\u5F97\u50CF\u4E2A\u7272\u53E3\u3002\u5728\u804C\u4E1A\u53D1\u5C55\u4E0A\u5404\u79CD\u8FF7\u832B\u548C\u5F77\u5FA8\uFF0C\u5B8C\u5168\u770B\u4E0D\u5230\u672A\u6765\u7684\u5E0C\u671B\uFF0C\u66F4\u522B\u8BF4\u53EF\u4EE5\u6210\u4E3A\u4E00\u4E2A\u624B\u827A\u4EBA\u7528\u81EA\u5DF1\u7684\u6280\u80FD\u53D8\u73B0\u4E86\u3002</p><p>2\u3001\u6210\u4E3A\u4E00\u4E2A\u624B\u827A\u4EBA\uFF0C\u52A8\u624B\u80FD\u529B\u662F\u5F88\u91CD\u8981\u7684\uFF0C\u56E0\u4E3A\u5728\u89E3\u51B3\u4EFB\u4F55\u4E00\u4E2A\u5177\u4F53\u95EE\u9898\u7684\u65F6\u5019\uFF0C\u6709\u6CA1\u6709\u52A8\u624B\u80FD\u529B\u5C31\u6210\u4E3A\u4E86\u5173\u952E\u3002\u8FD9\u4E5F\u662F\u6211\u4E00\u76F4\u5728\u5199\u4EE3\u7801\u7684\u539F\u56E0\uFF0C\u4EE3\u7801\u91CC\u5168\u662F\u7EC6\u8282\uFF0C\u7EC6\u8282\u662F\u9B54\u9B3C\uFF0C\u53EA\u6709\u4E86\u89E3\u4E86\u7EC6\u8282\uFF0C\u4F60\u624D\u80FD\u63D0\u51FA\u66F4\u597D\u6216\u662F\u66F4\u9760\u8C31\u3001\u53EF\u4EE5\u843D\u5730\u7684\u89E3\u51B3\u65B9\u6848\uFF0C\u800C\u4E0D\u662F\u4E00\u4E9B\u7B3C\u7EDF\u548C\u6A21\u7CCA\u7684\u4E1C\u897F\u3002\u8FD9\u592A\u91CD\u8981\u4E86\u3002</p><p>3\u3001\u4F1A\u6323\u94B1\u7684\u4EBA\u4E00\u5B9A\u662F\u4F1A\u6295\u8D44\u7684\u4EBA\u3002\u6211\u4E00\u76F4\u8BA4\u4E3A\uFF0C\u6700\u5B9D\u8D35\u7684\u8D22\u5BCC\u5E76\u4E0D\u662F\u94B1\uFF0C\u800C\u662F\u4F60\u7684\u65F6\u95F4\uFF0C\u65F6\u95F4\u6BD4\u94B1\u66F4\u5B9D\u8D35\uFF0C\u56E0\u4E3A\u94B1\u4F60\u4E0D\u7528\u8FD8\u5728\u90A3\u91CC\uFF0C\u800C\u65F6\u95F4\u4F60\u4E0D\u7528\u5C31\u6D6A\u8D39\u6389\u4E86\u3002\u4F60\u628A\u4F60\u7684\u65F6\u95F4\u6295\u8D44\u5728\u54EA\u4E9B\u5730\u65B9\uFF0C\u5C31\u610F\u5473\u7740\u4F60\u672A\u6765\u4F1A\u8D70\u4EC0\u4E48\u6837\u7684\u8DEF\u3002\u6240\u4EE5\uFF0C\u5229\u7528\u597D\u4F60\u7684\u65F6\u95F4\uFF0C\u6295\u5230\u4E00\u4E9B\u6709\u610F\u4E49\u7684\u5730\u65B9\u5427\u3002</p><p>4\u3001\u5728\u5B66\u4E60\u65B0\u6280\u672F\u7684\u8FC7\u7A0B\u4E00\u5B9A\u8981\u591A\u95EE\u81EA\u5DF1\u4E24\u4E2A\u95EE\u9898\u3002\u7B2C\u4E00\uFF0C\u8FD9\u4E2A\u6280\u672F\u89E3\u51B3\u4EC0\u4E48\u95EE\u9898\uFF1F\u4E3A\u4EC0\u4E48\u522B\u7684\u540C\u7C7B\u6280\u672F\u505A\u4E0D\u5230\uFF1F\u7B2C\u4E8C\uFF0C\u4E3A\u4EC0\u4E48\u662F\u8FD9\u6837\u89E3\u51B3\u7684\uFF1F\u6709\u6CA1\u6709\u66F4\u597D\u7684\u65B9\u5F0F\uFF1F\u5343\u4E07\u522B\u4E00\u4E0A\u6765\u5C31\u9677\u5165\u7EC6\u8282\u3002</p><p>5\u3001\u53EA\u6709\u90A3\u4E9B\u5728\u9AD8\u901F\u53D1\u5C55\u7684\u516C\u53F8\uFF0C\u6280\u672F\u4EBA\u5458\u7684\u4EF7\u503C\u624D\u80FD\u88AB\u6700\u5927\u5316\u5730\u4F53\u73B0\u51FA\u6765\u3002\u6BD4\u8F83\u597D\u7684\u6210\u957F\u8DEF\u5F84\u662F\uFF0C\u5148\u8FDB\u5165\u5927\u516C\u53F8\u5B66\u4E60\u5927\u516C\u53F8\u7684\u6280\u672F\u548C\u6210\u529F\u7684\u7ECF\u9A8C\u65B9\u6CD5\uFF0C\u7136\u540E\u518D\u627E\u5230\u9AD8\u901F\u6210\u957F\u7684\u516C\u53F8\uFF0C\u8FD9\u6837\u4F60\u5C31\u53EF\u4EE5\u5B9E\u73B0\u81EA\u5DF1\u66F4\u591A\u7684\u4EF7\u503C\u3002\u5F53\u7136\uFF0C\u8FD9\u91CC\u5E76\u4E0D\u6392\u9664\u5728\u5927\u516C\u53F8\u4E2D\u627E\u5230\u9AD8\u901F\u53D1\u5C55\u7684\u4E1A\u52A1\u3002</p><p>6\u3001\u89E3\u51B3\u4E00\u4E2A\u95EE\u9898\u901A\u5E38\u6709\u5F88\u591A\u79CD\u65B9\u6CD5\u3002\u5E26\u7740\u5F00\u653E\u7684\u5FC3\u6001\uFF0C\u4E0D\u62D8\u6CE5\u4E8E\u4E00\u4E2A\u5E73\u53F0\u3001\u4E00\u79CD\u8BED\u8A00\uFF0C\u5F80\u5F80\u80FD\u5E26\u6765\u66F4\u591A\u601D\u8003\uFF0C\u4E5F\u80FD\u5F97\u5230\u66F4\u597D\u7684\u7ED3\u679C\u3002\u800C\u4E14\uFF0C\u80FD\u5728\u4E0D\u540C\u7684\u65B9\u6CD5\u548C\u65B9\u6848\u95F4\u505A\u6BD4\u8F83\uFF0C\u6BD4\u8F83\u5B83\u4EEC\u7684\u4F18\u7F3A\u70B9\uFF0C\u90A3\u4E48\u4F60\u4F1A\u77E5\u9053\u5728\u4EC0\u4E48\u6837\u7684\u573A\u666F\u4E0B\u7528\u4EC0\u4E48\u6837\u7684\u65B9\u6848\uFF0C\u4F60\u5C31\u4F1A\u6BD4\u4E00\u822C\u4EBA\u80FD\u591F\u6709\u66F4\u5168\u9762\u548C\u66F4\u5B8C\u6574\u7684\u601D\u8DEF\u3002</p><p>7\u3001\u4EC0\u4E48\u662F\u6280\u672F\u9886\u5BFC\u529B\uFF1F\u4E00\u53E5\u8BDD\u603B\u7ED3\u5C31\u662F\u9047\u5230\u95EE\u9898\u65F6\uFF0C\u4F60\u80FD\u591F\u63D0\u4F9B\u6709\u5EFA\u8BBE\u6027\u7684\u610F\u89C1\u3002</p><p>8\u3001\u5BF9\u4E8E\u6211\u4EEC\u6BCF\u4E2A\u4EBA\u6765\u8BF4\uFF0C\u4F5C\u54C1\u96C6\u4F1A\u6BD4\u7B80\u5386\u66F4\u6709\u53C2\u8003\u610F\u4E49\u3002\u6240\u4EE5\uFF0C\u5728\u81EA\u5DF1\u7684\u7B80\u5386\u4E2D\u5E94\u8BE5\u653E\u4E0A\u81EA\u5DF1\u7684\u4E00\u4E9B\u9879\u76EE\u7ECF\u5386\uFF0C\u6216\u662F\u4E00\u4E9B\u5F00\u6E90\u8F6F\u4EF6\u7684\u8D21\u732E\u3002</p><p>9\u3001\u5982\u679C\u4E00\u4E2A\u516C\u53F8\u6216\u8005\u4E00\u4E2A\u4EBA\u80FD\u591F\u6293\u4F4F\u6280\u672F\u7EA2\u5229\uFF0C\u90A3\u5C31\u4F1A\u6BD4\u5176\u5B83\u516C\u53F8\u6216\u4E2A\u4EBA\u6709\u66F4\u5927\u7684\u5F71\u54CD\u529B\u3002\u4E00\u65E6\u672A\u6765\u884C\u4E1A\u9700\u6C42\u5F15\u7206\uFF0C\u90A3\u4E48\u8FD9\u4E2A\u516C\u53F8\u6216\u8FD9\u4E2A\u4EBA\u7684\u5F71\u54CD\u529B\u5C31\u4F1A\u5F62\u6210\u4E00\u4E2A\u6BD4\u8F83\u5927\u7684\u62A4\u57CE\u6CB3\uFF0C\u5E76\u53EF\u4EE5\u5FEB\u901F\u5730\u4ECE\u4E2D\u83B7\u53D6\u7ECF\u6D4E\u5229\u76CA\u3002</p><p>10\u3001Go \u8BED\u8A00\u4E0D\u4F1A\u541E\u98DF\u5E95\u5C42\u5230 C \u548C C++ \u90A3\u4E2A\u7EA7\u522B\u7684\uFF0C\u4E5F\u4E0D\u4F1A\u541E\u98DF\u5230\u4E0A\u5C42\u5982 Java \u4E1A\u52A1\u5C42\u7684\u9879\u76EE\u3002Go \u8BED\u8A00\u80FD\u541E\u98DF\u7684\u4E00\u5B9A\u662F PaaS \u4E0A\u7684\u9879\u76EE\uFF0C\u6BD4\u5982\u4E00\u4E9B\u6D88\u606F\u7F13\u5B58\u4E2D\u95F4\u4EF6\u3001\u670D\u52A1\u53D1\u73B0\u3001\u670D\u52A1\u4EE3\u7406\u3001\u63A7\u5236\u7CFB\u7EDF\u3001Agent\u3001\u65E5\u5FD7\u6536\u96C6\u7B49\u7B49\uFF0C\u4ED6\u4EEC\u6CA1\u6709\u590D\u6742\u7684\u4E1A\u52A1\u573A\u666F\uFF0C\u4E5F\u5230\u4E0D\u4E86\u7279\u522B\u5E95\u5C42\uFF08\u5982\u64CD\u4F5C\u7CFB\u7EDF\uFF09\u7684\u8F6F\u4EF6\u9879\u76EE\u6216\u5DE5\u5177\u3002\u800C C \u548C C++ \u4F1A\u88AB\u6253\u5230\u66F4\u5E95\u5C42\uFF0CJava \u4F1A\u88AB\u6253\u5230\u66F4\u4E0A\u5C42\u7684\u4E1A\u52A1\u5C42\u3002\u8FD9\u662F\u6211\u7684\u4E00\u4E2A\u5224\u65AD\u3002</p><p>11\u3001\u627E\u5230\u81EA\u5DF1\u7684\u957F\u5904\uFF0C\u4E0D\u65AD\u5730\u5728\u81EA\u5DF1\u7684\u957F\u5904\u4E0A\u53D1\u5C55\u81EA\u6211\u3002\u77E5\u9053\u81EA\u5DF1\u51E0\u65A4\u51E0\u4E24\u624D\u80FD\u6E05\u695A\u81EA\u5DF1\u9002\u5408\u5E72\u4EC0\u4E48\u3002\u4E0D\u7136\uFF0C\u76EE\u6807\u8BBE\u7F6E\u5F97\u8FC7\u9AD8\u81EA\u5DF1\u8FBE\u4E0D\u5230\uFF0C\u53CD\u800C\u8BA9\u81EA\u5DF1\u96BE\u53D7\u3002\u5728\u804C\u573A\u4E0A\uFF0C\u5BA1\u89C6\u81EA\u5DF1\u7684\u6700\u4F73\u65B9\u5F0F\uFF0C\u5C31\u662F\u9694\u4E09\u5DEE\u4E94\u5C31\u51FA\u53BB\u9762\u8BD5\u4E00\u628A\uFF0C\u770B\u770B\u81EA\u5DF1\u5728\u5E02\u573A\u4E0A\u80FD\u591F\u5230\u4EC0\u4E48\u6837\u7684\u7EA7\u522B\u3002\u5982\u679C\u4F60\u8D85\u8FC7\u4E86\u8EAB\u8FB9\u7684\u5927\u591A\u6570\u4EBA\uFF0C\u4F60\u4E0D\u59A8\u9009\u62E9\u5F97\u6FC0\u8FDB\u4E00\u4E9B\u5192\u9669\u4E00\u4E9B\uFF0C\u5426\u5219\uFF0C\u8FD8\u662F\u6309\u90E8\u5C31\u73ED\u5730\u6765\u5427\u3002</p><p>12\u300130-40 \u5C81\uFF0C\u8FD9\u662F\u4EBA\u751F\u53D1\u5C55\u7684\u9636\u6BB5\u3002\u56E0\u4E3A\u6574\u4E2A\u793E\u4F1A\u4E00\u5B9A\u4F1A\u628A\u793E\u4F1A\u7684\u91CD\u62C5\u4EA4\u7ED9\u8FD9\u7FA4\u4EBA\uFF0C30-40 \u5C81\u7684\u4EBA\u5E74\u5BCC\u529B\u5F3A\uFF0C\u65E2\u6709\u7ECF\u9A8C\u53C8\u6709\u7CBE\u529B\uFF0C\u8FD8\u6562\u60F3\u6562\u5E72\uFF0C\u6240\u4EE5\u8FD9\u7FA4\u4EBA\u624D\u662F\u6574\u4E2A\u793E\u4F1A\u7684\u4E2D\u6D41\u7825\u67F1\u3002\u5728\u8FD9\u4E2A\u9636\u6BB5\uFF0C\u4F60\u9700\u8981\u660E\u786E\u81EA\u5DF1\u594B\u6597\u7684\u65B9\u5411\uFF0C\u9700\u8981\u505A\u6709\u6311\u6218\u7684\u4E8B\u513F\uFF0C\u9700\u8981\u63D0\u5347\u81EA\u5DF1\u7684\u6280\u672F\u9886\u5BFC\u529B\u3002</p><p>13\u3001\u52A0\u73ED\u592A\u4E25\u91CD\u5B8C\u5168\u6CA1\u6709\u65F6\u95F4\u5B66\u4E60\uFF0C\u600E\u4E48\u529E\uFF1F\u6211\u89C9\u5F97\uFF0C\u53EF\u80FD\u5E76\u4E0D\u5728\u4E8E\u52A0\u73ED\u548C\u5DE5\u4F5C\u5F3A\u5EA6\u5927\u5230\u6CA1\u65F6\u95F4\uFF0C\u5173\u952E\u770B\u4F60\u5BF9\u5B66\u4E60\u6709\u591A\u5C11\u7684\u6E34\u671B\u7A0B\u5EA6\uFF0C\u5BF9\u8981\u5B66\u7684\u4E1C\u897F\u6709\u591A\u5927\u7684\u70ED\u60C5\u3002\u8FD9\u70B9\u662F\u975E\u5E38\u91CD\u8981\u7684\uFF0C\u56E0\u4E3A\u5B66\u4E60\u8FD9\u4E8B\u5176\u5B9E\u633A\u53CD\u4EBA\u6027\u7684\u3002\u53CD\u4EBA\u6027\u7684\u4E8B\u57FA\u672C\u4E0A\u90FD\u662F\u8981\u4ED8\u51FA\u5F88\u591A\uFF0C\u800C\u4E14\u8FD8\u8981\u575A\u6301\u5F88\u4E45\u3002\u6240\u4EE5\uFF0C\u5982\u679C\u5BF9\u5B66\u4E60\u6CA1\u6709\u6E34\u671B\u7684\u8BDD\uFF0C\u6216\u662F\u4E0D\u80FD\u4ECE\u5B66\u4E60\u4E2D\u627E\u5230\u5FEB\u4E50\u7684\u8BDD\uFF0C\u90A3\u4E48\u5176\u5B9E\u662F\u5F88\u96BE\u575A\u6301\u7684\uFF0C\u65E0\u8BBA\u4F60\u6709\u6CA1\u6709\u65F6\u95F4\u3002</p><p>14\u3001\u522B\u4EBA\u613F\u610F\u8DDF\u968F\u4F60\uFF0C\u613F\u610F\u548C\u4F60\u5171\u4E8B\uFF0C\u6709\u4E00\u90E8\u5206\u539F\u56E0\u662F\u4F60\u80FD\u591F\u7ED9\u522B\u4EBA\u5E26\u6765\u66F4\u591A\u7684\u53EF\u80FD\u6027\u548C\u673A\u4F1A\uFF0C\u522B\u4EBA\u89C9\u5F97\u548C\u4F60\u5728\u4E00\u8D77\u80FD\u591F\u6210\u957F\uFF0C\u80FD\u591F\u8FDB\u6B65\uFF0C\u4F60\u80FD\u591F\u5E26\u7740\u5927\u5BB6\u5230\u8FBE\u66F4\u8FDC\u7684\u5730\u65B9\u3002\u5E2E\u52A9\u522B\u4EBA\u5176\u5B9E\u5C31\u662F\u5E2E\u52A9\u81EA\u5DF1\uFF0C\u6210\u5C31\u4ED6\u4EBA\u5176\u5B9E\u4E5F\u662F\u5728\u6210\u5C31\u81EA\u5DF1\uFF0C\u8FD9\u5C31\u50CF\u4E00\u4E2A\u597D\u7684\u8DB3\u7403\u961F\u4E00\u6837\uFF0C\u7403\u961F\u4E2D\u7684\u4EBA\u90FD\u4E92\u76F8\u7ED9\u961F\u53CB\u521B\u9020\u673A\u4F1A\uFF0C\u6574\u4E2A\u56E2\u961F\u6210\u529F\u4E86\uFF0C\u7403\u961F\u7684\u6BCF\u4E2A\u4EBA\u4E5F\u5C31\u6210\u529F\u4E86\u3002\u4F5C\u4E3A\u4E00\u4E2A\u597D\u7684 Leader\uFF0C\u4F60\u4E00\u5B9A\u8981\u5728\u56E2\u961F\u4E2D\u521B\u9020\u597D\u8FD9\u6837\u7684\u6587\u5316\u548C\u98CE\u6C14\u3002</p><p>15\u3001\u6211\u89C9\u5F97\u4ECA\u5929\u5927\u591A\u6570\u7684\u4F1A\u90FD\u5F00\u9519\u4E86\u3002\u5728\u4F1A\u4E0A\u629B\u51FA\u95EE\u9898\uFF0C\u8FD8\u662F\u5F00\u653E\u6027\u7684\u95EE\u9898\uFF0C\u7136\u540E\u516C\u8BF4\u516C\u6709\u7406\uFF0C\u5A46\u8BF4\u5A46\u6709\u7406\uFF0C\u4EFB\u5927\u5BB6\u81EA\u7531\u53D1\u6325\uFF0C\u5404\u79CD\u8DD1\u9898\u8DD1\u504F\uFF0C\u6700\u540E\u8FD8\u6CA1\u6709\u4EFB\u4F55\u7684\u7B54\u6848\u3002\u5F00\u4F1A\uFF0C\u4E0D\u662F\u8BA8\u8BBA\u95EE\u9898\uFF0C\u800C\u662F\u8BA8\u8BBA\u65B9\u6848\uFF0C\u5F00\u4F1A\u4E0D\u662F\u8981\u6709\u8BAE\u9898\uFF0C\u800C\u662F\u8981\u6709\u8BAE\u6848\u3002</p><p>16\u3001\u5B66\u4E60\u662F\u9006\u4EBA\u6027\u7684\u4E8B\u513F\uFF0C\u4F46\u5982\u679C\u5728\u5B66\u4E60\u8FC7\u7A0B\u4E2D\u4E0D\u65AD\u5730\u6709\u6B63\u53CD\u9988\uFF0C\u5C31\u66F4\u5229\u4E8E\u6211\u4EEC\u575A\u6301\u4E0B\u53BB\u3002\u8981\u8BA9\u81EA\u5DF1\u6709\u6B63\u53CD\u9988\uFF0C\u90A3\u5C31\u9700\u8981\u628A\u65F6\u95F4\u82B1\u5728\u6709\u4EF7\u503C\u7684\u5730\u65B9\uFF0C\u6BD4\u5982\uFF0C\u89E3\u51B3\u81EA\u5DF1\u548C\u4ED6\u4EBA\u7684\u75DB\u70B9\uFF0C\u8FD9\u6837\u4F60\u4F1A\u6536\u83B7\u522B\u4EBA\u7684\u8D5E\u626C\u548C\u9F13\u52B1\u3002</p><p>17\u3001\u5728\u53C2\u52A0\u5DE5\u4F5C\u7684\u8FD9 20 \u5E74\u65F6\u95F4\u91CC\uFF0C\u6211\u53D1\u73B0\uFF0C\u5F88\u591A\u7A0B\u5E8F\u5458\u90FD\u628A\u65F6\u95F4\u90FD\u6D6A\u8D39\u5728\u4E86\u67E5\u9519\u4E0A\u3002\u7A76\u5176\u6839\u672C\u539F\u56E0\u5C31\u662F\u57FA\u7840\u77E5\u8BC6\u4E0D\u5B8C\u6574\uFF0C\u6CA1\u6709\u597D\u597D\u5730\u628A\u6280\u672F\u76F8\u5173\u7684\u7528\u6237\u6587\u6863\u8BFB\u5B8C\u5C31\u4ED3\u4FC3\u4E0A\u624B\u505A\u4E8B\u60C5\u4E86\u3002\u5176\u5B9E\u53EA\u8981\u628A\u57FA\u7840\u6253\u624E\u5B9E\uFF0C\u8BA4\u771F\u8BFB\u4E00\u4E0B\u6587\u6863\uFF0C\u4F60\u4F1A\u7701\u51FA\u5F88\u591A\u5F88\u591A\u7684\u65F6\u95F4\u3002\u7CFB\u7EDF\u5730\u5B66\u4E60\u4E00\u95E8\u6280\u672F\u662F\u975E\u5E38\u5173\u952E\u7684\uFF0C\u6240\u4EE5\u8FD9\u4E2A\u65F6\u95F4\u662F\u503C\u5F97\u6295\u8D44\u7684\u3002</p><p>18\u3001\u4E00\u4E2A\u53EF\u4EE5\u6301\u4E45\u7684\u5174\u8DA3\uFF0C\u6216\u662F\u53EF\u4EE5\u57F9\u517B\u51FA\u6765\u7684\u5174\u8DA3\uFF0C\u540E\u9762\u90FD\u6709\u4E00\u4E2A\u6BD4\u8F83\u672C\u8D28\u7684\u4E1C\u897F\uFF0C\u5176\u5B9E\u5C31\u662F\u6210\u5C31\u611F\uFF0C\u4ED6\u662F\u4F60\u575A\u6301\u6216\u8005\u52AA\u529B\u7684\u6700\u76F4\u63A5\u7684\u6B63\u53CD\u9988\u3002\u4E5F\u5C31\u662F\u8BF4\uFF0C\u5174\u8DA3\u53EA\u662F\u5F00\u59CB\uFF0C\u800C\u80FD\u8BA9\u4EBA\u4E0D\u65AD\u6295\u5165\u65F6\u95F4\u548C\u7CBE\u529B\u7684\u5219\u662F\u6B63\u53CD\u9988\uFF0C\u662F\u6210\u5C31\u611F\u3002</p><p>19\u3001\u4E0E\u5176\u82B1\u65F6\u95F4\u5728 Git \u534F\u540C\u5DE5\u4F5C\u6D41\u4E0A\uFF0C\u8FD8\u4E0D\u5982\u628A\u65F6\u95F4\u82B1\u5728\u8C03\u6574\u8F6F\u4EF6\u67B6\u6784\u548C\u81EA\u52A8\u5316\u8F6F\u4EF6\u751F\u4EA7\u548C\u8FD0\u7EF4\u6D41\u7A0B\u4E0A\u6765\uFF0C\u8FD9\u624D\u662F\u771F\u6B63\u7B80\u5316\u534F\u540C\u5DE5\u4F5C\u6D41\u7A0B\u7684\u6839\u672C\u3002</p><p>20\u3001\u7F16\u7A0B\u8303\u5F0F\u5176\u5B9E\u5C31\u662F\u7A0B\u5E8F\u7684\u6307\u5BFC\u601D\u60F3\uFF0C\u5B83\u4E5F\u4EE3\u8868\u4E86\u8FD9\u95E8\u8BED\u8A00\u7684\u8BBE\u8BA1\u65B9\u5411\uFF0C\u6211\u4EEC\u5E76\u4E0D\u80FD\u8BF4\u54EA\u79CD\u8303\u5F0F\u66F4\u4E3A\u8D85\u524D\uFF0C\u53EA\u80FD\u8BF4\u5404\u6709\u5343\u79CB\u3002\u6BD4\u5982 C \u8BED\u8A00\u5C31\u662F\u8FC7\u7A0B\u5F0F\u7684\u7F16\u7A0B\u8BED\u8A00\uFF0C\u50CF C \u8BED\u8A00\u8FD9\u6837\u7684\u8FC7\u7A0B\u5F0F\u7F16\u7A0B\u8BED\u8A00\u4F18\u70B9\u662F\u5E95\u5C42\u7075\u6D3B\u800C\u4E14\u9AD8\u6548\uFF0C\u7279\u522B\u9002\u5408\u5F00\u53D1\u8FD0\u884C\u8F83\u5FEB\u4E14\u5BF9\u7CFB\u7EDF\u8D44\u6E90\u5229\u7528\u7387\u8981\u6C42\u8F83\u9AD8\u7684\u7A0B\u5E8F\uFF0C\u4F46\u6211\u4E0A\u9762\u629B\u51FA\u7684\u95EE\u9898\u5B83\u5728\u540E\u6765\u4E5F\u6CA1\u6709\u8BD5\u56FE\u53BB\u89E3\u51B3\uFF0C\u56E0\u4E3A\u7F16\u7A0B\u8303\u5F0F\u7684\u9009\u62E9\u57FA\u672C\u5DF2\u7ECF\u51B3\u5B9A\u4E86\u5B83\u7684\u201C\u547D\u8FD0\u201D\u3002</p><p>21\u3001\u4E9A\u9A6C\u900A\u7684 AWS \u624D\u4F1A\u628A Design for Failure \u4F5C\u4E3A\u5176\u4E03\u5927 Design Principle \u7684\u91CD\u70B9\u3002\u8FD9\u544A\u8BC9\u6211\u4EEC\uFF0C\u4E0D\u8981\u5C1D\u8BD5\u7740\u53BB\u907F\u514D\u6545\u969C\uFF0C\u800C\u662F\u8981\u628A\u5904\u7406\u6545\u969C\u7684\u4EE3\u7801\u5F53\u6210\u6B63\u5E38\u7684\u529F\u80FD\u505A\u5728\u67B6\u6784\u91CC\u5199\u5728\u4EE3\u7801\u91CC\u3002\u56E0\u4E3A\u6211\u4EEC\u8981\u5E72\u7684\u4E8B\u513F\u5C31\u662F\u60F3\u5C3D\u4E00\u5207\u624B\u6BB5\u6765\u964D\u4F4E MTTR\u2014\u2014\u6545\u969C\u7684\u4FEE\u590D\u65F6\u95F4\u3002</p><p>22\u3001\u9694\u79BB\u8BBE\u8BA1\u5BF9\u5E94\u7684\u5355\u8BCD\u662F Bulkheads\uFF0C\u4E2D\u6587\u7FFB\u8BD1\u4E3A\u9694\u677F\u3002\u4F46\u5176\u5B9E\uFF0C\u8FD9\u4E2A\u672F\u8BED\u662F\u7528\u5728\u9020\u8239\u4E0A\u7684\uFF0C\u4E5F\u5C31\u662F\u8239\u8231\u91CC\u9632\u6F0F\u6C34\u7684\u9694\u677F\u3002\u4E00\u822C\u7684\u8239\u65E0\u8BBA\u5927\u5C0F\u90FD\u4F1A\u6709\u8FD9\u4E2A\u4E1C\u897F\uFF0C\u5927\u4E00\u70B9\u7684\u8239\u90FD\u4F1A\u628A\u8239\u8231\u9694\u6210\u82E5\u5E72\u4E2A\u7A7A\u95F4\u3002\u8FD9\u6837\uFF0C\u5982\u679C\u8239\u8231\u6F0F\u6C34\uFF0C\u53EA\u4F1A\u8FDB\u5230\u4E00\u4E2A\u5C0F\u7A7A\u95F4\u91CC\uFF0C\u4E0D\u4F1A\u8BA9\u6574\u4E2A\u8239\u8231\u90FD\u8FDB\u6C34\u800C\u5BFC\u81F4\u6574\u8258\u8239\u90FD\u6C89\u4E86\u3002</p><p>23\u3001\u901A\u8BAF\u4E00\u822C\u6765\u8BF4\u5206\u540C\u6B65\u548C\u5F02\u6B65\u4E24\u79CD\u3002\u540C\u6B65\u901A\u8BAF\u5C31\u50CF\u6253\u7535\u8BDD\uFF0C\u9700\u8981\u5B9E\u65F6\u54CD\u5E94\uFF0C\u800C\u5F02\u6B65\u901A\u8BAF\u5C31\u50CF\u53D1\u90AE\u4EF6\uFF0C\u4E0D\u9700\u8981\u9A6C\u4E0A\u56DE\u590D\u3002\u5404\u6709\u5343\u79CB\uFF0C\u6211\u4EEC\u5F88\u96BE\u8BF4\u8C01\u6BD4\u8C01\u597D\u3002\u4F46\u662F\u5728\u9762\u5BF9\u8D85\u9AD8\u5410\u541E\u91CF\u7684\u573A\u666F\u4E0B\uFF0C\u5F02\u6B65\u5904\u7406\u5C31\u6BD4\u540C\u6B65\u5904\u7406\u6709\u6BD4\u8F83\u5927\u7684\u4F18\u52BF\u4E86\uFF0C\u8FD9\u5C31\u597D\u50CF\u4E00\u4E2A\u4EBA\u4E0D\u53EF\u80FD\u540C\u65F6\u63A5\u6253\u5F88\u591A\u7535\u8BDD\uFF0C\u4F46\u662F\u4ED6\u53EF\u4EE5\u540C\u65F6\u63A5\u6536\u5F88\u591A\u7684\u7535\u5B50\u90AE\u4EF6\u4E00\u6837\u3002</p><p>24\u3001\u6240\u8C13\u5E42\u7B49\u6027\u8BBE\u8BA1\uFF0C\u5C31\u662F\u8BF4\uFF0C\u4E00\u6B21\u548C\u591A\u6B21\u8BF7\u6C42\u67D0\u4E00\u4E2A\u8D44\u6E90\u5E94\u8BE5\u5177\u6709\u540C\u6837\u7684\u526F\u4F5C\u7528\u3002\u7528\u6570\u5B66\u7684\u8BED\u8A00\u6765\u8868\u8FBE\u5C31\u662F\uFF1Af(x) = f(f(x))\u3002</p><p>25\u3001ACID \u7684\u73A9\u6CD5\u5C31\u662F\uFF0C\u5927\u5BB6\u5728\u4E70\u540C\u4E00\u672C\u4E66\u7684\u8FC7\u7A0B\u4E2D\uFF0C\u6BCF\u4E2A\u7528\u6237\u7684\u8D2D\u4E70\u8BF7\u6C42\u90FD\u9700\u8981\u628A\u5E93\u5B58\u9501\u4F4F\uFF0C\u7B49\u51CF\u5B8C\u5E93\u5B58\u540E\uFF0C\u628A\u9501\u91CA\u653E\u51FA\u6765\uFF0C\u540E\u7EED\u7684\u4EBA\u624D\u80FD\u8FDB\u884C\u8D2D\u4E70\u3002\u4E8E\u662F\uFF0C\u5728 ACID \u7684\u73A9\u6CD5\u4E0B\uFF0C\u6211\u4EEC\u5728\u540C\u4E00\u65F6\u95F4\u4E0D\u53EF\u80FD\u6709\u591A\u4E2A\u7528\u6237\u4E0B\u5355\uFF0C\u6211\u4EEC\u7684\u8BA2\u5355\u6D41\u7A0B\u9700\u8981\u6709\u6392\u961F\u7684\u60C5\u51B5\uFF0C\u8FD9\u6837\u4E00\u6765\uFF0C\u6211\u4EEC\u5C31\u4E0D\u53EF\u80FD\u505A\u51FA\u6027\u80FD\u6BD4\u8F83\u9AD8\u7684\u7CFB\u7EDF\u6765\u3002BASE \u7684\u73A9\u6CD5\u662F\uFF0C\u5927\u5BB6\u90FD\u53EF\u4EE5\u540C\u65F6\u4E0B\u5355\uFF0C\u8FD9\u4E2A\u65F6\u5019\u4E0D\u9700\u8981\u53BB\u771F\u6B63\u5730\u5206\u914D\u5E93\u5B58\uFF0C\u7136\u540E\u7CFB\u7EDF\u5F02\u6B65\u5730\u5904\u7406\u8BA2\u5355\uFF0C\u800C\u4E14\u662F\u6279\u91CF\u7684\u5904\u7406\u3002\u56E0\u4E3A\u4E0B\u5355\u7684\u65F6\u5019\u6CA1\u6709\u771F\u6B63\u53BB\u6263\u51CF\u5E93\u5B58\uFF0C\u6240\u4EE5\uFF0C\u6709\u53EF\u80FD\u4F1A\u6709\u8D85\u5356\u7684\u60C5\u51B5\u3002\u800C\u540E\u53F0\u7684\u7CFB\u7EDF\u4F1A\u5F02\u6B65\u5730\u5904\u7406\u8BA2\u5355\u65F6\uFF0C\u53D1\u73B0\u5E93\u5B58\u6CA1\u6709\u4E86\uFF0C\u4E8E\u662F\u624D\u4F1A\u544A\u8BC9\u7528\u6237\u4F60\u6CA1\u6709\u8D2D\u4E70\u6210\u529F\u3002</p><p>26\u3001\u7194\u65AD\u673A\u5236\u8FD9\u4E2A\u8BCD\u5BF9\u4F60\u6765\u8BF4\u80AF\u5B9A\u4E0D\u964C\u751F\uFF0C\u5B83\u7684\u7075\u611F\u6765\u6E90\u4E8E\u6211\u4EEC\u7535\u95F8\u4E0A\u7684\u201C\u4FDD\u9669\u4E1D\u201D\uFF0C\u5F53\u7535\u538B\u6709\u95EE\u9898\u65F6\uFF08\u6BD4\u5982\u77ED\u8DEF\uFF09\uFF0C\u81EA\u52A8\u8DF3\u95F8\uFF0C\u6B64\u65F6\u7535\u8DEF\u5C31\u4F1A\u65AD\u5F00\uFF0C\u6211\u4EEC\u7684\u7535\u5668\u5C31\u4F1A\u53D7\u5230\u4FDD\u62A4\u3002\u4E0D\u7136\uFF0C\u4F1A\u5BFC\u81F4\u7535\u5668\u88AB\u70E7\u574F\uFF0C\u5982\u679C\u4EBA\u6CA1\u5728\u5BB6\u6216\u662F\u4EBA\u5728\u719F\u7761\u4E2D\uFF0C\u8FD8\u4F1A\u5BFC\u81F4\u706B\u707E\u3002\u6240\u4EE5\uFF0C\u5728\u7535\u8DEF\u4E16\u754C\u901A\u5E38\u90FD\u4F1A\u6709\u8FD9\u6837\u7684\u81EA\u6211\u4FDD\u62A4\u88C5\u7F6E\u3002</p><p>27\u3001\u6240\u8C13\u7684\u964D\u7EA7\u8BBE\u8BA1\uFF08Degradation\uFF09\uFF0C\u672C\u8D28\u662F\u4E3A\u4E86\u89E3\u51B3\u8D44\u6E90\u4E0D\u8DB3\u548C\u8BBF\u95EE\u91CF\u8FC7\u5927\u7684\u95EE\u9898\u3002\u5F53\u8D44\u6E90\u548C\u8BBF\u95EE\u91CF\u51FA\u73B0\u77DB\u76FE\u7684\u65F6\u5019\uFF0C\u5728\u6709\u9650\u7684\u8D44\u6E90\u4E0B\uFF0C\u4E3A\u4E86\u80FD\u591F\u625B\u4F4F\u5927\u91CF\u7684\u8BF7\u6C42\uFF0C\u6211\u4EEC\u5C31\u9700\u8981\u5BF9\u7CFB\u7EDF\u8FDB\u884C\u964D\u7EA7\u64CD\u4F5C\u3002\u4E5F\u5C31\u662F\u8BF4\uFF0C\u6682\u65F6\u727A\u7272\u6389\u4E00\u4E9B\u4E1C\u897F\uFF0C\u4EE5\u4FDD\u969C\u6574\u4E2A\u7CFB\u7EDF\u7684\u5E73\u7A33\u8FD0\u884C\u3002</p><p>28\u3001\u6280\u672F\u80FD\u529B\u7684\u74F6\u9888\uFF0C\u4EE5\u53CA\u6280\u672F\u592A\u591A\u5B66\u4E0D\u8FC7\u6765\uFF0C\u53EA\u4E0D\u8FC7\u662F\u4F60\u4E3A\u81EA\u5DF1\u7684\u80FD\u529B\u4E0D\u8DB3\u6216\u662F\u61D2\u60F0\u627E\u7684\u501F\u53E3\u7F62\u4E86\u3002\u6280\u672F\u7684\u4E1C\u897F\u90FD\u662F\u6B7B\u7684\uFF0C\u8FD9\u4E9B\u6B7B\u7684\u77E5\u8BC6\u53EA\u8981\u52AA\u529B\u5C31\u662F\u53EF\u4EE5\u5B66\u4F1A\u7684\u3002\u53EA\u4E0D\u8FC7\u806A\u660E\u7684\u4EBA\u82B1\u5F97\u65F6\u95F4\u5C11\uFF0C\u7B28\u70B9\u7684\u4EBA\u82B1\u5F97\u65F6\u95F4\u591A\u70B9\u7F62\u4E86\u3002\u8FD9\u5176\u4E2D\u7684\u65F6\u95F4\u5DEE\u8DDD\u4E3B\u8981\u662F\u7531\u5B66\u4E60\u65B9\u6CD5\u7684\u4E0D\u540C\uFF0C\u57FA\u7840\u77E5\u8BC6\u50A8\u5907\u7684\u4E0D\u540C\u51B3\u5B9A\u7684\u3002\u53EA\u8981\u4F60\u65B9\u6CD5\u5F97\u5F53\uFF0C\u591A\u82B1\u70B9\u65F6\u95F4\u5728\u57FA\u7840\u77E5\u8BC6\u4E0A\uFF0C\u4F1A\u8BA9\u4F60\u672A\u6765\u5B66\u4E60\u5E94\u7528\u77E5\u8BC6\u7684\u65F6\u95F4\u5927\u5927\u7F29\u77ED\u3002\u4EE5\u7EDD\u5927\u591A\u6570\u4EBA\u52AA\u529B\u7684\u7A0B\u5EA6\uFF0C\u548C\u4E3A\u81EA\u5DF1\u4E0D\u52AA\u529B\u627E\u501F\u53E3\u7684\u7A0B\u5EA6\u4E3A\u53C2\u8003\uFF0C\u53EA\u8981\u4F60\u575A\u6301\u6B63\u5E38\u7684\u5B66\u4E60\u5C31\u53EF\u4EE5\u8D85\u8FC7\u5927\u591A\u6570\u4EBA\u4E86\u3002</p><p>29\u3001\u8981\u5E94\u4ED8\u5E76\u901A\u8FC7\u9762\u8BD5\u5E76\u4E0D\u96BE\uFF0C\u4F46\u662F\uFF0C\u5343\u4E07\u4E0D\u8981\u5E94\u4ED8\u4F60\u7684\u4EBA\u751F\uFF0C\u4F60\u5B66\u6280\u672F\u4E0D\u662F\u7528\u6765\u9762\u8BD5\u7684\uFF0C\u5B83\u81F3\u5C11\u6765\u8BF4\u662F\u4F60\u8C0B\u751F\u7684\u6280\u80FD\uFF0C\u8981\u5C0A\u91CD\u81EA\u5DF1\u7684\u8C0B\u751F\u6280\u80FD\uFF0C\u8BF4\u4E0D\u5B9A\uFF0C\u54EA\u5929\u4F60\u8FD8\u8981\u7528\u8FD9\u4E9B\u6280\u80FD\u9020\u798F\u793E\u4F1A\u3001\u6539\u53D8\u4E16\u754C\u7684\u3002</p><p>30\u3001\u5B66\u4E60\u4E0D\u662F\u52AA\u529B\u8BFB\u66F4\u591A\u7684\u4E66\uFF0C\u76F2\u76EE\u8FFD\u6C42\u9605\u8BFB\u7684\u901F\u5EA6\u548C\u6570\u91CF\uFF0C\u8FD9\u4F1A\u8BA9\u4EBA\u4EA7\u751F\u4F4E\u5C42\u6B21\u7684\u52E4\u594B\u548C\u6210\u957F\u7684\u611F\u89C9\uFF0C\u8FD9\u53EA\u662F\u5728\u4F7F\u86EE\u529B\u3002\u8981\u601D\u8FA8\uFF0C\u8981\u8DF5\u884C\uFF0C\u8981\u603B\u7ED3\u548C\u5F52\u7EB3\uFF0C\u5426\u5219\uFF0C\u4F60\u53EA\u662F\u5728\u673A\u68B0\u5730\u91CD\u590D\u67D0\u4EF6\u4E8B\uFF0C\u800C\u4E0D\u4F1A\u6709\u8D28\u7684\u6210\u957F\u7684\u3002</p><p>31\u3001\u628A\u4F60\u770B\u5230\u548C\u5B66\u4E60\u5230\u7684\u4FE1\u606F\uFF0C\u5F52\u6574\u597D\uFF0C\u6392\u5217\u597D\uFF0C\u5173\u8054\u597D\uFF0C\u603B\u4E4B\u628A\u4FE1\u606F\u788E\u7247\u7ED9\u7ED3\u6784\u5316\u6389\uFF0C\u7136\u540E\u5728\u7ED3\u6784\u5316\u7684\u4FE1\u606F\u4E2D\uFF0C\u627E\u5230\u89C4\u5F8B\uFF0C\u627E\u5230\u76F8\u901A\u4E4B\u5904\uFF0C\u627E\u5230\u5171\u540C\u4E4B\u5904\uFF0C\u8FDB\u884C\u7B80\u5316\u3001\u5F52\u7EB3\u548C\u603B\u7ED3\uFF0C\u6700\u7EC8\u5F62\u6210\u4E00\u79CD\u5957\u8DEF\uFF0C\u4E00\u79CD\u6A21\u5F0F\uFF0C\u4E00\u79CD\u901A\u7528\u65B9\u6CD5\u3002</p><p>32\u3001\u575A\u6301\u4E5F\u4E0D\u662F\u8981\u82E6\u82E6\u5730\u575A\u6301\uFF0C\u6709\u5FAA\u73AF\u6709\u6210\u5C31\u611F\u7684\u575A\u6301\u624D\u662F\u771F\u6B63\u53EF\u4EE5\u6301\u7EED\u7684\u3002\u6240\u4EE5\uFF0C\u4E00\u65B9\u9762\u4F60\u8981\u628A\u4F60\u7684\u575A\u6301\u5F62\u6210\u6210\u679C\u6652\u51FA\u6765\uFF0C\u8BA9\u522B\u4EBA\u6765\u7ED9\u4F60\u70B9\u8D5E\uFF0C\u53E6\u4E00\u65B9\u9762\uFF0C\u4F60\u8FD8\u8981\u628A\u575A\u6301\u53D8\u6210\u4E00\u79CD\u4E60\u60EF\uFF0C\u5C31\u50CF\u5403\u996D\u559D\u6C34\u4E00\u6837\uFF0C\u4F60\u611F\u89C9\u4E0D\u5230\u592A\u591A\u7684\u6210\u672C\u4ED8\u51FA\u3002\u53EA\u6709\u505A\u5230\u8FD9\u4E24\u70B9\uFF0C\u4F60\u624D\u80FD\u591F\u771F\u6B63\u575A\u6301\u3002</p><p>33\u3001\u628A\u4F60\u65B0\u5B66\u7684\u77E5\u8BC6\u70B9\u5173\u8054\u5230\u5DF2\u77E5\u7684\u4E8B\u7269\u4E0A\u6765\u3002\u6BD4\u5982\uFF0C\u4F60\u5728\u5B66\u4E60 Go \u8BED\u8A00\uFF0C\u4F60\u5C31\u628A\u4E00\u4E9B\u77E5\u8BC6\u5173\u8054\u5230\u81EA\u5DF1\u5DF2\u7ECF\u5B66\u8FC7\u7684\u8BED\u8A00\u4E0A\u6BD4\u5982 C \u548C Java\u3002\u901A\u8FC7\u7C7B\u6BD4\uFF0C\u4F60\u4F1A\u5B66\u5F97\u66F4\u624E\u5B9E\uFF0C\u4E5F\u4F1A\u601D\u8003\u5F97\u66F4\u591A\u3002</p><p>34\u3001\u5BF9\u4E8E\u6280\u672F\u4EBA\u5458\u6765\u8BF4\uFF0C\u5176\u5B9E\uFF0CTalk \u548C Code \u662F\u540C\u6837\u91CD\u8981\u7684\uFF0C Talk \u662F\u4EBA\u5BF9\u4EBA\u8BF4\u7684\u8BDD\uFF0C\u800C Code \u4E5F\u4E0D\u4EC5\u4EC5\u53EA\u662F\u4EBA\u5BF9\u673A\u5668\u8BF4\u7684\u8BDD\uFF0C\u4E5F\u66F4\u662F\u53E6\u5916\u4E00\u79CD\u4EBA\u5BF9\u4EBA\u8BF4\u7684\u8BDD\uFF08\u56E0\u4E3A Code \u9700\u8981\u6613\u8BFB\u548C\u6613\u7EF4\u62A4\uFF0C\u5C31\u9700\u8981\u8BA9\u4EBA\u8BFB\u61C2\uFF09\u3002\u53EF\u89C1\uFF0C\u65E0\u8BBA\u662F Code \u8FD8\u662F Talk \u5176\u5B9E\u90FD\u662F\u8981\u548C\u4EBA\u4EA4\u6D41\u7684\uFF0CCode \u662F\u95F4\u63A5\u4EA4\u6D41\uFF0CTalk \u5219\u662F\u76F4\u63A5\u4EA4\u6D41\u3002</p><p>35\u3001\u6BCF\u4E2A\u4EBA\u90FD\u5E94\u8BE5\u4E3A\u81EA\u5DF1\u7684\u4E8B\u4E1A\u3001\u4E3A\u81EA\u5DF1\u7684\u7406\u60F3\u53BB\u6D3B\u4E00\u6B21\uFF0C\u8FFD\u9010\u81EA\u5DF1\u7684\u4E8B\u4E1A\u548C\u7406\u60F3\u5E76\u4E0D\u5BB9\u6613\uFF0C\u9700\u8981\u6709\u5F88\u5927\u4ED8\u51FA\uFF0C\u4E5F\u53EA\u6709\u4F60\u5185\u5FC3\u7684\u90A3\u4E2A\u7406\u60F3\u624D\u503C\u5F97\u8FD9\u4E48\u5927\u7684\u4ED8\u51FA\u2026\u2026</p><p>36\u3001\u6323\u94B1\u662F\u4E00\u4EF6\u5927\u5BB6\u90FD\u60F3\u505A\u7684\u4E8B\uFF0C\u4F46\u4F60\u5F97\u89E3\u51B3\u4E00\u4E2A\u5F88\u6838\u5FC3\u7684\u95EE\u9898\uFF0C\u90A3\u5C31\u662F\u4E3A\u4EC0\u4E48\u522B\u4EBA\u613F\u610F\u7ED9\u4F60\u94B1\uFF1F\u5BF9\u4E8E\u6323\u94B1\u8FD9\u4EF6\u4E8B\u7684\u770B\u6CD5\uFF0C\u6211\u4ECE\u5927\u5B66\u6BD5\u4E1A\u5230\u73B0\u5728\u90FD\u6CA1\u600E\u4E48\u53D8\u8FC7\uFF0C\u90A3\u5C31\u662F\u6211\u66F4\u591A\u5173\u6CE8\u600E\u4E48\u63D0\u9AD8\u81EA\u5DF1\u7684\u80FD\u529B\uFF0C\u8BA9\u81EA\u5DF1\u503C\u90A3\u4E2A\u4EF7\u94B1\uFF0C\u8BA9\u522B\u4EBA\u613F\u610F\u4ED8\u94B1\u3002\u53E6\u5916\uFF0C\u6211\u53D1\u73B0\u8D8A\u662F\u6709\u80FD\u529B\u7684\u4EBA\uFF0C\u5C31\u8D8A\u4E0D\u8BA1\u8F83\u4E00\u4E9B\u77ED\u671F\u5F97\u5931\uFF0C\u8D8A\u8BA1\u8F83\u77ED\u671F\u5F97\u5931\u7684\u4EBA\u5F80\u5F80\u90FD\u662F\u5F88\u5E73\u5EB8\u7684\u4EBA\u3002</p><p>37\u3001\u4ECE\u5BA2\u89C2\u89C4\u5F8B\u4E0A\u6765\u8BF4\uFF0C\u67D0\u4EF6\u4E8B\u7684\u8FDB\u6B65\u4E00\u5B9A\u4F1A\u548C\u73B0\u72B6\u6709\u4E00\u4E9B\u6469\u64E6\u3002\u6709\u7684\u4EBA\u5BB3\u6015\u6469\u64E6\u800C\u9009\u62E9\u5FCD\u8010\uFF0C\u6211\u5219\u4E0D\u662F\uFF0C\u6211\u89C9\u5F97\u4E0E\u522B\u4EBA\u7684\u6469\u64E6\u5E76\u4E0D\u53EF\u6015\uFF0C\u56E0\u4E3A\u5927\u5BB6\u7684\u76EE\u6807\u90FD\u662F\u57FA\u672C\u4E00\u81F4\u7684\uFF0C\u53EA\u662F\u505A\u4E8B\u7684\u6807\u51C6\u548C\u65B9\u5F0F\u4E0D\u4E00\u6837\uFF0C\u8FD9\u662F\u53EF\u4EE5\u6C9F\u901A\u548C\u76F8\u4E92\u7406\u89E3\u7684\u3002</p><p>38\u3001\u6211\u9009\u62E9\u4E86\u4E00\u4E2A\u6B63\u786E\u7684\u4E13\u4E1A\uFF08\u8BA1\u7B97\u673A\u79D1\u5B66\uFF09\uFF0C\u5F85\u5728\u4E86\u4E00\u4E2A\u6B63\u786E\u7684\u5E74\u4EE3\uFF08\u4FE1\u606F\u5316\u9769\u547D\uFF09\uFF0C\u8FD9\u6837\u7684\u201C\u72D7\u5C4E\u8FD0\u201D\u51E0\u767E\u5E74\u4E0D\u9047\uFF0C\u5982\u679C\u6211\u8FD8\u60A3\u5F97\u60A3\u5931\uFF0C\u90A3\u6211\u5C82\u4E0D\u8F9C\u8D1F\u4E86\u6D3B\u5728\u8FD9\u6837\u4E00\u4E2A\u523A\u6FC0\u7684\u65F6\u4EE3\uFF1F\uFF01\u6211\u53EA\u9700\u8981\u5728\u8FD9\u4E2A\u65F6\u4EE3\u4E2D\u505A\u6709\u4EF7\u503C\u7684\u4E8B\u5C31\u597D\u4E86\uFF01</p>",38);function _(r,d,n,i,s,c){return p(),a("div",null,[t])}o.render=_;export{l as __pageData,o as default};
