import{o as t,g as e,G as a}from"./framework.e6884fad.js";const l='{"title":"Event Loop","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u4E3A\u4EC0\u4E48JavaScript\u662F\u5355\u7EBF\u7A0B\uFF1F","slug":"\u4E3A\u4EC0\u4E48javascript\u662F\u5355\u7EBF\u7A0B\uFF1F"},{"level":2,"title":"\u4EFB\u52A1\u961F\u5217","slug":"\u4EFB\u52A1\u961F\u5217"}],"relativePath":"js/event_loop.md","lastUpdated":1684201617048}',o={},p=a('<h1 id="event-loop"><a class="header-anchor" href="#event-loop" aria-hidden="true">#</a> Event Loop</h1><h2 id="\u4E3A\u4EC0\u4E48javascript\u662F\u5355\u7EBF\u7A0B\uFF1F"><a class="header-anchor" href="#\u4E3A\u4EC0\u4E48javascript\u662F\u5355\u7EBF\u7A0B\uFF1F" aria-hidden="true">#</a> \u4E3A\u4EC0\u4E48JavaScript\u662F\u5355\u7EBF\u7A0B\uFF1F</h2><blockquote><p>JavaScript\u7684\u4E3B\u8981\u7528\u9014\u662F\u4E0E\u7528\u6237\u4E92\u52A8\uFF0C\u4EE5\u53CA\u64CD\u4F5CDOM\u3002\u8FD9\u51B3\u5B9A\u4E86\u5B83\u53EA\u80FD\u662F\u5355\u7EBF\u7A0B\uFF0C\u5426\u5219\u4F1A\u5E26\u6765\u5F88\u590D\u6742\u7684\u540C\u6B65\u95EE\u9898\u3002</p></blockquote><h2 id="\u4EFB\u52A1\u961F\u5217"><a class="header-anchor" href="#\u4EFB\u52A1\u961F\u5217" aria-hidden="true">#</a> \u4EFB\u52A1\u961F\u5217</h2><p>\u5355\u7EBF\u7A0B\u5C31\u610F\u5473\u7740\uFF0C\u6240\u6709\u4EFB\u52A1\u9700\u8981\u6392\u961F\uFF0C\u524D\u4E00\u4E2A\u4EFB\u52A1\u7ED3\u675F\uFF0C\u624D\u4F1A\u6267\u884C\u540E\u4E00\u4E2A\u4EFB\u52A1\u3002\u5982\u679C\u524D\u4E00\u4E2A\u4EFB\u52A1\u8017\u65F6\u5F88\u957F\uFF0C\u540E\u4E00\u4E2A\u4EFB\u52A1\u5C31\u4E0D\u5F97\u4E0D\u4E00\u76F4\u7B49\u7740\u3002</p><p>\u5982\u679C\u6392\u961F\u662F\u56E0\u4E3A\u8BA1\u7B97\u91CF\u5927\uFF0CCPU\u5FD9\u4E0D\u8FC7\u6765\uFF0C\u5012\u4E5F\u7B97\u4E86\uFF0C\u4F46\u662F\u5F88\u591A\u65F6\u5019CPU\u662F\u95F2\u7740\u7684\uFF0C\u56E0\u4E3AIO\u8BBE\u5907\uFF08\u8F93\u5165\u8F93\u51FA\u8BBE\u5907\uFF09\u5F88\u6162\uFF08\u6BD4\u5982Ajax\u64CD\u4F5C\u4ECE\u7F51\u7EDC\u8BFB\u53D6\u6570\u636E\uFF09\uFF0C\u4E0D\u5F97\u4E0D\u7B49\u7740\u7ED3\u679C\u51FA\u6765\uFF0C\u518D\u5F80\u4E0B\u6267\u884C\u3002</p><p>JavaScript\u8BED\u8A00\u7684\u8BBE\u8BA1\u8005\u610F\u8BC6\u5230\uFF0C\u8FD9\u65F6\u4E3B\u7EBF\u7A0B\u5B8C\u5168\u53EF\u4EE5\u4E0D\u7BA1IO\u8BBE\u5907\uFF0C\u6302\u8D77\u5904\u4E8E\u7B49\u5F85\u4E2D\u7684\u4EFB\u52A1\uFF0C\u5148\u8FD0\u884C\u6392\u5728\u540E\u9762\u7684\u4EFB\u52A1\u3002\u7B49\u5230IO\u8BBE\u5907\u8FD4\u56DE\u4E86\u7ED3\u679C\uFF0C\u518D\u56DE\u8FC7\u5934\uFF0C\u628A\u6302\u8D77\u7684\u4EFB\u52A1\u7EE7\u7EED\u6267\u884C\u4E0B\u53BB\u3002</p><p>\u4E8E\u662F\uFF0C\u6240\u6709\u4EFB\u52A1\u53EF\u4EE5\u5206\u6210\u4E24\u79CD\uFF0C\u4E00\u79CD\u662F\u540C\u6B65\u4EFB\u52A1\uFF08synchronous\uFF09\uFF0C\u53E6\u4E00\u79CD\u662F\u5F02\u6B65\u4EFB\u52A1\uFF08asynchronous\uFF09\u3002\u540C\u6B65\u4EFB\u52A1\u6307\u7684\u662F\uFF0C\u5728\u4E3B\u7EBF\u7A0B\u4E0A\u6392\u961F\u6267\u884C\u7684\u4EFB\u52A1\uFF0C\u53EA\u6709\u524D\u4E00\u4E2A\u4EFB\u52A1\u6267\u884C\u5B8C\u6BD5\uFF0C\u624D\u80FD\u6267\u884C\u540E\u4E00\u4E2A\u4EFB\u52A1\uFF1B\u5F02\u6B65\u4EFB\u52A1\u6307\u7684\u662F\uFF0C\u4E0D\u8FDB\u5165\u4E3B\u7EBF\u7A0B\u3001\u800C\u8FDB\u5165&quot;\u4EFB\u52A1\u961F\u5217&quot;\uFF08task queue\uFF09\u7684\u4EFB\u52A1\uFF0C\u53EA\u6709&quot;\u4EFB\u52A1\u961F\u5217&quot;\u901A\u77E5\u4E3B\u7EBF\u7A0B\uFF0C\u67D0\u4E2A\u5F02\u6B65\u4EFB\u52A1\u53EF\u4EE5\u6267\u884C\u4E86\uFF0C\u8BE5\u4EFB\u52A1\u624D\u4F1A\u8FDB\u5165\u4E3B\u7EBF\u7A0B\u6267\u884C\u3002</p><p>\u5177\u4F53\u6765\u8BF4\uFF0C\u5F02\u6B65\u6267\u884C\u7684\u8FD0\u884C\u673A\u5236\u5982\u4E0B\u3002\uFF08\u540C\u6B65\u6267\u884C\u4E5F\u662F\u5982\u6B64\uFF0C\u56E0\u4E3A\u5B83\u53EF\u4EE5\u88AB\u89C6\u4E3A\u6CA1\u6709\u5F02\u6B65\u4EFB\u52A1\u7684\u5F02\u6B65\u6267\u884C\u3002\uFF09</p><p>\uFF081\uFF09\u6240\u6709\u540C\u6B65\u4EFB\u52A1\u90FD\u5728\u4E3B\u7EBF\u7A0B\u4E0A\u6267\u884C\uFF0C\u5F62\u6210\u4E00\u4E2A\u6267\u884C\u6808\uFF08execution context stack\uFF09\u3002</p><p>\uFF082\uFF09\u4E3B\u7EBF\u7A0B\u4E4B\u5916\uFF0C\u8FD8\u5B58\u5728\u4E00\u4E2A&quot;\u4EFB\u52A1\u961F\u5217&quot;\uFF08task queue\uFF09\u3002\u53EA\u8981\u5F02\u6B65\u4EFB\u52A1\u6709\u4E86\u8FD0\u884C\u7ED3\u679C\uFF0C\u5C31\u5728&quot;\u4EFB\u52A1\u961F\u5217&quot;\u4E4B\u4E2D\u653E\u7F6E\u4E00\u4E2A\u4E8B\u4EF6\u3002</p><p>\uFF083\uFF09\u4E00\u65E6&quot;\u6267\u884C\u6808&quot;\u4E2D\u7684\u6240\u6709\u540C\u6B65\u4EFB\u52A1\u6267\u884C\u5B8C\u6BD5\uFF0C\u7CFB\u7EDF\u5C31\u4F1A\u8BFB\u53D6&quot;\u4EFB\u52A1\u961F\u5217&quot;\uFF0C\u770B\u770B\u91CC\u9762\u6709\u54EA\u4E9B\u4E8B\u4EF6\u3002\u90A3\u4E9B\u5BF9\u5E94\u7684\u5F02\u6B65\u4EFB\u52A1\uFF0C\u4E8E\u662F\u7ED3\u675F\u7B49\u5F85\u72B6\u6001\uFF0C\u8FDB\u5165\u6267\u884C\u6808\uFF0C\u5F00\u59CB\u6267\u884C\u3002</p><p>\uFF084\uFF09\u4E3B\u7EBF\u7A0B\u4E0D\u65AD\u91CD\u590D\u4E0A\u9762\u7684\u7B2C\u4E09\u6B65\u3002</p>',13);function r(c,s,n,u,i,d){return t(),e("div",null,[p])}o.render=r;export{l as __pageData,o as default};
